function sendMessage(){document.getElementById("bigWiki-on").checked?chrome.tabs.query({active:!0,currentWindow:!0},function(e){chrome.tabs.sendMessage(e[0].id,{bigWikiON:!0},function(e){console.log(e.message)})}):document.getElementById("bigWiki-off").checked&&chrome.tabs.query({active:!0,currentWindow:!0},function(e){chrome.tabs.sendMessage(e[0].id,{bigWikiON:!1},function(e){e&&console.log(e.message)})})}document.getElementById("big-switch").addEventListener("click",function(){sendMessage()});